import React, { useState } from 'react';
import { View, StyleSheet, Image, ScrollView, Alert } from 'react-native';
import { Input, Button, Text, Icon, CheckBox } from 'react-native-elements';
import * as ImagePicker from 'expo-image-picker';
import { storage, db } from '../../utils/firebase';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import { addDoc, collection, serverTimestamp } from 'firebase/firestore';
import { useTheme } from '../../components/common/ThemeProvider';
import themeBase from '../../theme';

const SeriesAddScreen = ({ navigation }) => {
  const { theme } = useTheme();
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [genre, setGenre] = useState('');
  const [releaseYear, setReleaseYear] = useState('');
  const [subscriptionPlan, setSubscriptionPlan] = useState('');
  const [isFeatured, setIsFeatured] = useState(false);
  const [thumbnail, setThumbnail] = useState(null);
  const [banner, setBanner] = useState(null);
  const [uploading, setUploading] = useState(false);

  const pickThumbnail = async () => {
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (status !== 'granted') {
      Alert.alert('Permission required', 'Camera roll permission is required to select thumbnail.');
      return;
    }
    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      quality: 0.8,
    });
    if (!result.canceled) {
      setThumbnail(result.assets[0].uri);
    }
  };

  const pickBanner = async () => {
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (status !== 'granted') {
      Alert.alert('Permission required', 'Camera roll permission is required to select banner.');
      return;
    }
    const result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      quality: 0.8,
      aspect: [16, 9],
    });
    if (!result.canceled) {
      setBanner(result.assets[0].uri);
    }
  };

  const handleSave = async () => {
    if (!title.trim()) {
      Alert.alert('Validation', 'Please enter a title for the series.');
      return;
    }
    setUploading(true);
    try {
      let thumbnailUrl = null;
      let bannerUrl = null;
      if (thumbnail) {
        const response = await fetch(thumbnail);
        const blob = await response.blob();
        const fileName = `series_${Date.now()}`;
        const storageRef = ref(storage, `series/${fileName}`);
        await uploadBytes(storageRef, blob);
        thumbnailUrl = await getDownloadURL(storageRef);
      }
      if (banner) {
        const responseB = await fetch(banner);
        const blobB = await responseB.blob();
        const fileNameB = `banner_${Date.now()}`;
        const storageRefB = ref(storage, `series_banners/${fileNameB}`);
        await uploadBytes(storageRefB, blobB);
        bannerUrl = await getDownloadURL(storageRefB);
      }
      await addDoc(collection(db, 'series'), {
        title: title.trim(),
        description: description.trim(),
        genre: genre.trim(),
        releaseYear: releaseYear.trim(),
        subscriptionPlan: subscriptionPlan.trim(),
        isFeatured,
        thumbnailUrl,
        bannerUrl,
        createdAt: serverTimestamp(),
      });
      Alert.alert('Success', 'Series created successfully.');
      navigation.navigate('Series');
    } catch (error) {
      console.error('Error creating series:', error);
      Alert.alert('Error', 'Could not create series. Please try again.');
    } finally {
      setUploading(false);
    }
  };

  return (
    <ScrollView contentContainerStyle={[styles.container, { backgroundColor: theme.colors.backgroundLight }]}>
      <Text h4 style={styles.header}>Add New Series</Text>
      <Input
        placeholder="Title *"
        value={title}
        onChangeText={setTitle}
        leftIcon={{ name: 'edit', type: 'feather', color: theme.colors.textSecondary }}
      />
      <Input
        placeholder="Description"
        value={description}
        onChangeText={setDescription}
        multiline
        leftIcon={{ name: 'align-left', type: 'feather', color: theme.colors.textSecondary }}
      />
      <Input
        placeholder="Genre"
        value={genre}
        onChangeText={setGenre}
        leftIcon={{ name: 'tag', type: 'feather', color: theme.colors.textSecondary }}
      />
      <Input
        placeholder="Release Year"
        value={releaseYear}
        onChangeText={setReleaseYear}
        keyboardType="numeric"
        leftIcon={{ name: 'calendar', type: 'feather', color: theme.colors.textSecondary }}
      />
      <Input
        placeholder="Subscription Plan"
        value={subscriptionPlan}
        onChangeText={setSubscriptionPlan}
        leftIcon={{ name: 'credit-card', type: 'feather', color: theme.colors.textSecondary }}
      />
      <CheckBox
        title="Featured Series"
        checked={isFeatured}
        onPress={() => setIsFeatured(!isFeatured)}
        containerStyle={{ backgroundColor: 'transparent', borderWidth: 0 }}
      />
      <Button
        icon={<Icon name="image" type="feather" color="#fff" />}
        title={thumbnail ? 'Change Thumbnail' : 'Pick Thumbnail'}
        onPress={pickThumbnail}
        containerStyle={styles.button}
        buttonStyle={{ backgroundColor: theme.colors.primary }}
      />
      {thumbnail && <Image source={{ uri: thumbnail }} style={styles.preview} />}
      <Button
        icon={<Icon name="image" type="feather" color="#fff" />}
        title={banner ? 'Change Banner' : 'Pick Banner'}
        onPress={pickBanner}
        containerStyle={styles.button}
        buttonStyle={{ backgroundColor: theme.colors.accent }}
      />
      {banner && <Image source={{ uri: banner }} style={styles.previewBanner} />}
      <Button
        title="Cancel"
        type="outline"
        onPress={() => navigation.goBack()}
        containerStyle={styles.button}
        buttonStyle={{ borderColor: theme.colors.accent }}
        titleStyle={{ color: theme.colors.accent }}
      />
      <Button
        title={uploading ? 'Saving...' : 'Save Series'}
        onPress={handleSave}
        disabled={uploading}
        containerStyle={styles.button}
        buttonStyle={{ backgroundColor: theme.colors.accent }}
      />
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    padding: themeBase.spacing.medium,
    flexGrow: 1,
  },
  header: {
    marginBottom: themeBase.spacing.medium,
    alignSelf: 'center',
    color: themeBase.colors.primary,
    fontSize: themeBase.typography.fontSize.header,
  },
  button: {
    marginVertical: themeBase.spacing.small,
  },
  preview: {
    width: '100%',
    height: 200,
    borderRadius: themeBase.borderRadius.medium,
    marginTop: themeBase.spacing.small,
  },
  previewBanner: {
    width: '100%',
    height: 120,
    borderRadius: themeBase.borderRadius.medium,
    marginTop: themeBase.spacing.small,
  },
});

export default SeriesAddScreen;
