import React, { useState } from 'react';
import { View, StyleSheet, ScrollView } from 'react-native';
import { Button, Text } from 'react-native-elements';
import theme from '../../theme';
import Sidebar from '../../components/common/Sidebar';
import HeaderBar from '../../components/common/HeaderBar';
import { ThemeProvider, useTheme } from '../../components/common/ThemeProvider';

const NotificationWizardScreen = ({ navigation }) => {
  const [step, setStep] = useState(1);
  const { isDarkMode, toggleTheme, theme: currentTheme } = useTheme();

  const renderStep = () => {
    switch (step) {
      case 1:
        return <Text>Step 1: Basics (name, title, body, media, link)</Text>;
      case 2:
        return <Text>Step 2: Audience selection</Text>;
      case 3:
        return <Text>Step 3: Scheduling options</Text>;
      case 4:
        return <Text>Step 4: Advanced settings</Text>;
      case 5:
        return <Text>Step 5: Review & Send</Text>;
      default:
        return null;
    }
  };

  return (
    <View style={[styles.container, { backgroundColor: currentTheme.colors.background }]}> 
      <HeaderBar onToggleTheme={toggleTheme} isDarkMode={isDarkMode} />
      <View style={styles.body}>
        <Sidebar navigation={navigation} activeSection="publishing" />
        <ScrollView contentContainerStyle={styles.contentContainer}>
          {renderStep()}
          <View style={styles.buttonRow}>
            {step > 1 && <Button title="Back" type="outline" onPress={() => setStep(step - 1)} />}
            {step < 5 && <Button title="Next" onPress={() => setStep(step + 1)} />}
            {step === 5 && <Button title="Publish" onPress={() => {/* TODO: handle publish */}} />}
          </View>
        </ScrollView>
      </View>
    </View>
  );
};

const NotificationWizardScreenWithTheme = (props) => (
  <ThemeProvider>
    <NotificationWizardScreen {...props} />
  </ThemeProvider>
);

const styles = StyleSheet.create({
  container: { flex: 1, height: '100%' },
  body: { flex: 1, flexDirection: 'row' },
  contentContainer: { flex: 1, padding: theme.spacing.large },
  buttonRow: { flexDirection: 'row', justifyContent: 'space-between', marginTop: theme.spacing.medium },
});

export default NotificationWizardScreenWithTheme;
