import React, { useEffect, useState } from 'react';
import { ScrollView, View, StyleSheet } from 'react-native';
import { Text, Card, Button, Icon } from 'react-native-elements';
import { useNavigation } from '@react-navigation/native';
import theme from '../../theme';
import { db } from '../../utils/firebase';
import { collection, getDocs } from 'firebase/firestore';

const DashboardScreen = () => {
  const navigation = useNavigation();
  const [stats, setStats] = useState({ series: 0, episodes: 0, users: 0 });

  useEffect(() => {
    const fetchStats = async () => {
      const [seriesSnap, episodesSnap, usersSnap] = await Promise.all([
        getDocs(collection(db, 'series')),
        getDocs(collection(db, 'episodes')),
        getDocs(collection(db, 'users'))
      ]);
      setStats({ series: seriesSnap.size, episodes: episodesSnap.size, users: usersSnap.size });
    };
    fetchStats();
  }, []);

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text h3 style={styles.header}>Admin Dashboard</Text>
      <View style={styles.row}>
        <Card containerStyle={[styles.card, { backgroundColor: theme.colors.accent }] }>
          <Icon name="tv" type="font-awesome" color={theme.colors.white} size={28} />
          <Text style={styles.cardTitle}>Series</Text>
          <Text style={styles.cardValue}>{stats.series}</Text>
          <Button
            title="Manage"
            onPress={() => navigation.navigate('Series')}
            buttonStyle={styles.cardButton}
          />
          <Button
            title="Add Series"
            type="outline"
            onPress={() => navigation.navigate('SeriesAdd')}
            buttonStyle={[styles.cardButton, { marginTop: theme.spacing.small }]}
            titleStyle={{ color: theme.colors.textSecondary }}
          />
        </Card>
        <Card containerStyle={[styles.card, { backgroundColor: theme.colors.primary }] }>
          <Icon name="video-camera" type="font-awesome" color={theme.colors.white} size={28} />
          <Text style={styles.cardTitle}>Episodes</Text>
          <Text style={styles.cardValue}>{stats.episodes}</Text>
          <Button title="Manage" onPress={() => navigation.navigate('Episodes')} buttonStyle={styles.cardButton} />
        </Card>
      </View>
      <View style={styles.row}>
        <Card containerStyle={[styles.card, { backgroundColor: theme.colors.success }] }>
          <Icon name="users" type="font-awesome" color={theme.colors.white} size={28} />
          <Text style={styles.cardTitle}>Users</Text>
          <Text style={styles.cardValue}>{stats.users}</Text>
          <Button title="Manage" onPress={() => navigation.navigate('Users')} buttonStyle={styles.cardButton} />
        </Card>
        <Card containerStyle={[styles.card, { backgroundColor: theme.colors.warning }] }>
          <Icon name="bar-chart" type="font-awesome" color={theme.colors.white} size={28} />
          <Text style={styles.cardTitle}>Analytics</Text>
          <Text style={styles.cardValue}>View</Text>
          <Button title="View" onPress={() => navigation.navigate('Analytics')} buttonStyle={styles.cardButton} />
        </Card>
      </View>
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: {
    padding: theme.spacing.large,
    backgroundColor: theme.colors.background,
  },
  header: {
    color: theme.colors.text,
    marginBottom: theme.spacing.large,
    alignSelf: 'center',
  },
  row: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    marginBottom: theme.spacing.large,
  },
  card: {
    flex: 1,
    borderRadius: theme.borderRadius.medium,
    alignItems: 'center',
    justifyContent: 'center',
    paddingVertical: theme.spacing.medium,
    marginHorizontal: theme.spacing.small,
  },
  cardTitle: {
    color: theme.colors.white,
    marginTop: theme.spacing.small,
    fontSize: theme.typography.fontSize.medium,
    fontWeight: theme.typography.fontWeight.bold,
  },
  cardValue: {
    color: theme.colors.white,
    fontSize: theme.typography.fontSize.xxlarge,
    fontWeight: theme.typography.fontWeight.black,
    marginVertical: theme.spacing.small,
  },
  cardButton: {
    backgroundColor: theme.colors.backgroundLight,
    borderRadius: theme.borderRadius.small,
    paddingHorizontal: theme.spacing.large,
  },
});

export default DashboardScreen;
