import React, { useState, useEffect } from 'react';
import { View, StyleSheet, ScrollView, ActivityIndicator } from 'react-native';
import { Text, ListItem, Badge } from 'react-native-elements';
import { useNavigation } from '@react-navigation/native';
import HeaderBar from '../../components/common/HeaderBar';
import Sidebar from '../../components/common/Sidebar';
import { ThemeProvider, useTheme } from '../../components/common/ThemeProvider';
import paymentsApi from '../../api/paymentsApi';
import baseTheme from '../../theme';

const PaymentManagementScreen = () => {
  const navigation = useNavigation();
  const { theme } = useTheme();
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeSection, setActiveSection] = useState('payments');

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const data = await paymentsApi.getPayments();
        setPayments(data);
      } catch (error) {
        console.error('Error fetching payments:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchPayments();
  }, []);

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.background }]}>      
      <HeaderBar
        onToggleTheme={() => {}}
        isDarkMode={false}
        notificationCount={0}
        onLogout={() => {}}
        onSettings={() => {}}
      />
      <View style={styles.body}>
        <Sidebar
          navigation={navigation}
          activeSection={activeSection}
          onSectionChange={setActiveSection}
        />
        <ScrollView style={styles.content} contentContainerStyle={{ padding: baseTheme.spacing.large }}>
          <Text h4 style={[styles.title, { color: theme.colors.primary }]}>Payment Management</Text>
          {loading ? (
            <ActivityIndicator size="large" color={theme.colors.primary} />
          ) : (
            payments.map((p) => (
              <ListItem bottomDivider key={p.transactionId} containerStyle={{ backgroundColor: theme.colors.backgroundLight }}>
                <ListItem.Content>
                  <ListItem.Title style={{ color: theme.colors.text }}>Txn ID: {p.transactionId}</ListItem.Title>
                  <ListItem.Subtitle style={{ color: theme.colors.textSecondary }}>{p.userEmail}</ListItem.Subtitle>
                </ListItem.Content>
                <View style={styles.rightInfo}>
                  <Text style={{ color: theme.colors.success, fontWeight: 'bold' }}>${p.amount}</Text>
                  <Badge value={p.status} status={p.status === 'Paid' ? 'success' : 'warning'} containerStyle={{ marginLeft: 8 }} />
                </View>
              </ListItem>
            ))
          )}
        </ScrollView>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: { flex: 1 },
  body: { flex: 1, flexDirection: 'row' },
  content: { flex: 1 },
  title: { marginBottom: baseTheme.spacing.medium, alignSelf: 'center' },
  rightInfo: { flexDirection: 'row', alignItems: 'center' },
});

const PaymentManagementScreenWithTheme = (props) => (
  <ThemeProvider>
    <PaymentManagementScreen {...props} />
  </ThemeProvider>
);

export default PaymentManagementScreenWithTheme;
