import React, { useState, useEffect } from 'react';
import { View, StyleSheet, ScrollView, TouchableOpacity } from 'react-native';
import { Text, Button, Icon, Card, SearchBar } from 'react-native-elements';
import { useNavigation } from '@react-navigation/native';
import themeBase from '../../theme';
import { ThemeProvider, useTheme } from '../../components/common/ThemeProvider';
import Sidebar from '../../components/common/Sidebar';
import HeaderBar from '../../components/common/HeaderBar';
import { ToastContainer } from '../../components/common/Toast';
import { Dimensions } from 'react-native';

const MOCK_USERS = [
  { id: '1', name: 'John Doe', email: 'john.doe@example.com', role: 'Admin', status: 'Active', lastLogin: '2023-05-01' },
  { id: '2', name: 'Jane Smith', email: 'jane.smith@example.com', role: 'Editor', status: 'Inactive', lastLogin: '2023-04-15' },
  { id: '3', name: 'Bob Johnson', email: 'bob.johnson@example.com', role: 'Viewer', status: 'Active', lastLogin: '2023-05-10' },
  { id: '4', name: 'Alice Brown', email: 'alice.brown@example.com', role: 'Editor', status: 'Active', lastLogin: '2023-05-02' },
  { id: '5', name: 'Charlie Davis', email: 'charlie.davis@example.com', role: 'Viewer', status: 'Inactive', lastLogin: '2023-03-20' },
];

const UsersContent = () => {
  const [users, setUsers] = useState(MOCK_USERS);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredUsers, setFilteredUsers] = useState(MOCK_USERS);
  
  useEffect(() => {
    console.log('UsersScreen rendered');
    // Filter users based on search query
    const filtered = users.filter(user => 
      user.name.toLowerCase().includes(searchQuery.toLowerCase()) || 
      user.email.toLowerCase().includes(searchQuery.toLowerCase()) ||
      user.role.toLowerCase().includes(searchQuery.toLowerCase())
    );
    setFilteredUsers(filtered);
  }, [searchQuery, users]);

  const handleSearch = (query) => {
    setSearchQuery(query);
  };

  const handleAddUser = () => {
    console.log('Add user clicked');
    // Add new user logic here
  };

  const handleEditUser = (userId) => {
    console.log('Edit user clicked', userId);
    // Edit user logic here
  };

  const handleDeleteUser = (userId) => {
    console.log('Delete user clicked', userId);
    // Delete user logic here
  };

  const handleStatusChange = (userId, newStatus) => {
    console.log('Status change', userId, newStatus);
    // Change user status logic here
    const updatedUsers = users.map(user => 
      user.id === userId ? {...user, status: newStatus} : user
    );
    setUsers(updatedUsers);
  };

  return (
    <View style={styles.contentContainer}>
      <View style={styles.headerSection}>
        <Text style={styles.title}>User Management</Text>
        <Button
          icon={<Icon name="plus" type="font-awesome" color="#fff" size={14} style={{marginRight: 10}} />}
          title="Add User"
          onPress={handleAddUser}
          buttonStyle={styles.addButton}
        />
      </View>
      
      <Card containerStyle={styles.card}>
        <View style={styles.filterSection}>
          <SearchBar
            placeholder="Search users..."
            onChangeText={handleSearch}
            value={searchQuery}
            containerStyle={styles.searchContainer}
            inputContainerStyle={styles.searchInputContainer}
            round
            lightTheme
          />
        </View>
        
        <ScrollView horizontal style={styles.tableContainer}>
          <View>
            <View style={styles.tableHeader}>
              <Text style={[styles.headerCell, { width: 200 }]}>Name</Text>
              <Text style={[styles.headerCell, { width: 250 }]}>Email</Text>
              <Text style={[styles.headerCell, { width: 120 }]}>Role</Text>
              <Text style={[styles.headerCell, { width: 120 }]}>Status</Text>
              <Text style={[styles.headerCell, { width: 150 }]}>Last Login</Text>
              <Text style={[styles.headerCell, { width: 200 }]}>Actions</Text>
            </View>
            
            <ScrollView style={styles.tableBody}>
              {filteredUsers.map((user) => (
                <View key={user.id} style={styles.tableRow}>
                  <Text style={[styles.cell, { width: 200 }]}>{user.name}</Text>
                  <Text style={[styles.cell, { width: 250 }]}>{user.email}</Text>
                  <Text style={[styles.cell, { width: 120 }]}>{user.role}</Text>
                  <View style={[styles.cell, { width: 120 }]}>
                    <Text style={[
                      styles.statusBadge, 
                      user.status === 'Active' ? styles.activeBadge : styles.inactiveBadge
                    ]}>
                      {user.status}
                    </Text>
                  </View>
                  <Text style={[styles.cell, { width: 150 }]}>{user.lastLogin}</Text>
                  <View style={[styles.cell, { width: 200, flexDirection: 'row' }]}>
                    <Button
                      title="Edit"
                      onPress={() => handleEditUser(user.id)}
                      buttonStyle={styles.editButton}
                      titleStyle={styles.buttonTitle}
                    />
                    <Button
                      title={user.status === 'Active' ? 'Deactivate' : 'Activate'}
                      onPress={() => handleStatusChange(
                        user.id, 
                        user.status === 'Active' ? 'Inactive' : 'Active'
                      )}
                      buttonStyle={[
                        styles.statusButton,
                        user.status === 'Active' ? styles.deactivateButton : styles.activateButton
                      ]}
                      titleStyle={styles.buttonTitle}
                    />
                    <Button
                      title="Delete"
                      onPress={() => handleDeleteUser(user.id)}
                      buttonStyle={styles.deleteButton}
                      titleStyle={styles.buttonTitle}
                    />
                  </View>
                </View>
              ))}
            </ScrollView>
          </View>
        </ScrollView>
      </Card>
    </View>
  );
};

const UsersScreen = () => {
  const navigation = useNavigation();
  const [activeSection, setActiveSection] = useState('users');
  const { isDarkMode, toggleTheme, theme } = useTheme ? useTheme() : { isDarkMode: false, toggleTheme: () => {}, theme: themeBase };
  
  // Responsive layout
  const isMobile = Dimensions.get('window').width < 768;
  
  useEffect(() => {
    console.log('UsersScreen mounted');
  }, []);

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.background }]}>
      <HeaderBar
        onToggleTheme={toggleTheme}
        isDarkMode={isDarkMode}
        notificationCount={0}
        onLogout={() => {}}
        onSettings={() => {}}
      />
      <View style={styles.body}>
        {!isMobile && (
          <Sidebar
            navigation={navigation}
            activeSection={activeSection}
            onSectionChange={setActiveSection}
          />
        )}
        <View style={styles.mainContent}>
          <UsersContent />
        </View>
      </View>
      <ToastContainer />
    </View>
  );
};

const UsersScreenWithTheme = (props) => (
  <ThemeProvider>
    <UsersScreen {...props} />
  </ThemeProvider>
);

const styles = StyleSheet.create({
  container: {
    flex: 1,
    minHeight: '100vh',
  },
  body: {
    flex: 1,
    flexDirection: 'row',
    minHeight: 'calc(100vh - 64px)',
  },
  mainContent: {
    flex: 1,
    padding: themeBase.spacing.large,
    backgroundColor: 'transparent',
  },
  contentContainer: {
    flex: 1,
  },
  headerSection: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 20,
  },
  title: {
    fontSize: 24,
    fontWeight: 'bold',
    color: themeBase.colors.text,
  },
  addButton: {
    backgroundColor: themeBase.colors.success,
    borderRadius: 4,
    paddingHorizontal: 15,
  },
  card: {
    backgroundColor: themeBase.colors.backgroundLight,
    borderRadius: 8,
    padding: 0,
    overflow: 'hidden',
    flex: 1,
  },
  filterSection: {
    padding: 15,
    borderBottomWidth: 1,
    borderBottomColor: themeBase.colors.border,
  },
  searchContainer: {
    backgroundColor: 'transparent',
    borderTopWidth: 0,
    borderBottomWidth: 0,
    padding: 0,
  },
  searchInputContainer: {
    backgroundColor: themeBase.colors.background,
  },
  tableContainer: {
    flex: 1,
  },
  tableHeader: {
    flexDirection: 'row',
    paddingVertical: 10,
    paddingHorizontal: 15,
    backgroundColor: themeBase.colors.background,
    borderBottomWidth: 1,
    borderBottomColor: themeBase.colors.border,
  },
  headerCell: {
    fontWeight: 'bold',
    color: themeBase.colors.text,
  },
  tableBody: {
    flex: 1,
  },
  tableRow: {
    flexDirection: 'row',
    borderBottomWidth: 1,
    borderBottomColor: themeBase.colors.border,
    paddingVertical: 12,
    paddingHorizontal: 15,
  },
  cell: {
    color: themeBase.colors.textSecondary,
    justifyContent: 'center',
  },
  statusBadge: {
    paddingHorizontal: 8,
    paddingVertical: 3,
    borderRadius: 12,
    fontSize: 12,
    fontWeight: 'bold',
    textAlign: 'center',
    alignSelf: 'flex-start',
  },
  activeBadge: {
    backgroundColor: 'rgba(0, 200, 0, 0.2)',
    color: 'green',
  },
  inactiveBadge: {
    backgroundColor: 'rgba(200, 0, 0, 0.2)',
    color: 'darkred',
  },
  editButton: {
    backgroundColor: themeBase.colors.primary,
    marginRight: 4,
    paddingHorizontal: 8,
    height: 30,
  },
  statusButton: {
    marginRight: 4,
    paddingHorizontal: 8,
    height: 30,
  },
  activateButton: {
    backgroundColor: themeBase.colors.success,
  },
  deactivateButton: {
    backgroundColor: themeBase.colors.warning,
  },
  deleteButton: {
    backgroundColor: themeBase.colors.error,
    paddingHorizontal: 8,
    height: 30,
  },
  buttonTitle: {
    fontSize: 12,
  },
});

export default UsersScreenWithTheme;
