import React, { createContext, useContext, useEffect, useState } from 'react';
import { Provider as PaperProvider, DefaultTheme, DarkTheme } from 'react-native-paper';
import AsyncStorage from '@react-native-async-storage/async-storage';
import baseTheme from '../../theme';

const ThemeContext = createContext();

const customLightTheme = {
  ...DefaultTheme,
  colors: {
    ...DefaultTheme.colors,
    ...baseTheme.colors,
    background: '#f5f7fa',
    text: '#222',
    primary: baseTheme.colors.primary,
    accent: baseTheme.colors.accent,
  },
};

const customDarkTheme = {
  ...DarkTheme,
  colors: {
    ...DarkTheme.colors,
    ...baseTheme.colors,
    background: '#181818',
    text: '#fff',
    primary: baseTheme.colors.primary,
    accent: baseTheme.colors.accent,
  },
};

export const ThemeProvider = ({ children }) => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [theme, setTheme] = useState(customLightTheme);

  useEffect(() => {
    AsyncStorage.getItem('DASHBOARD_THEME').then((val) => {
      if (val === 'dark') {
        setIsDarkMode(true);
        setTheme(customDarkTheme);
      }
    });
  }, []);

  const toggleTheme = () => {
    setIsDarkMode((prev) => {
      const next = !prev;
      setTheme(next ? customDarkTheme : customLightTheme);
      AsyncStorage.setItem('DASHBOARD_THEME', next ? 'dark' : 'light');
      return next;
    });
  };

  return (
    <ThemeContext.Provider value={{ isDarkMode, toggleTheme, theme }}>
      <PaperProvider theme={theme}>{children}</PaperProvider>
    </ThemeContext.Provider>
  );
};

export const useTheme = () => useContext(ThemeContext);
