import React, { useState } from 'react';
import { ScrollView, StyleSheet, Alert, View, ActivityIndicator } from 'react-native';
import { Input, Button, Text, Icon } from 'react-native-elements';
import { useNavigation } from '@react-navigation/native';
import baseTheme from '../../theme';
import { useTheme, ThemeProvider } from '../../components/common/ThemeProvider';
import { Picker } from '@react-native-picker/picker';
import axios from 'axios';

const NotificationAddScreen = () => {
  const navigation = useNavigation();
  const { theme } = useTheme();
  const [title, setTitle] = useState('');
  const [message, setMessage] = useState('');
  const [audience, setAudience] = useState('');
  const [type, setType] = useState('');
  const [scheduleDate, setScheduleDate] = useState('');
  const [audienceOptions] = useState(['All Users', 'Subscribed Users', 'New Users', 'Region-Based']);
  const [typeOptions] = useState(['Push', 'Email', 'In-App']);
  const [sending, setSending] = useState(false);

  const handleSave = async () => {
    if (!title || !message) {
      Alert.alert('Validation error', 'Title and message are required');
      return;
    }
    setSending(true);
    try {
      await axios.post('https://your-backend.com/api/notifications', { title, message, audience, type, scheduleDate });
      Alert.alert('Success', 'Notification sent successfully');
      navigation.navigate('Notifications');
    } catch (error) {
      Alert.alert('Error', error.response?.data?.message || 'Failed to send notification');
    } finally {
      setSending(false);
    }
  };

  return (
    <ScrollView contentContainerStyle={[styles.container, { backgroundColor: theme.colors.background }] }>
      <Text h4 style={[styles.header, { color: theme.colors.text }]}>Create New Notification</Text>
      <Input
        placeholder="Title"
        placeholderTextColor={theme.colors.textSecondary}
        inputStyle={{ color: theme.colors.text }}
        value={title}
        onChangeText={setTitle}
      />
      <Input
        placeholder="Message"
        placeholderTextColor={theme.colors.textSecondary}
        inputStyle={{ color: theme.colors.text }}
        value={message}
        onChangeText={setMessage}
        multiline
      />
      <Text style={[styles.label, { color: theme.colors.text }]}>Audience</Text>
      <Picker selectedValue={audience} onValueChange={setAudience} style={styles.picker}>
        {audienceOptions.map(opt => <Picker.Item label={opt} value={opt} key={opt} />)}
      </Picker>
      <Text style={[styles.label, { color: theme.colors.text }]}>Type</Text>
      <Picker selectedValue={type} onValueChange={setType} style={styles.picker}>
        {typeOptions.map(opt => <Picker.Item label={opt} value={opt} key={opt} />)}
      </Picker>
      <Text style={[styles.label, { color: theme.colors.text }]}>Schedule Date</Text>
      <Input
        placeholder="Schedule Date (YYYY-MM-DD HH:mm)"
        placeholderTextColor={theme.colors.textSecondary}
        inputStyle={{ color: theme.colors.text }}
        value={scheduleDate}
        onChangeText={setScheduleDate}
        leftIcon={{ type: 'feather', name: 'calendar', color: theme.colors.primary }}
      />
      <Text style={[styles.previewHeader, { color: theme.colors.text }]}>Preview</Text>
      <View style={styles.previewBox}>
        <Text style={styles.previewTitle}>{title || 'Title Preview'}</Text>
        <Text style={styles.previewMessage}>{message || 'Message Preview'}</Text>
      </View>
      {sending ? (
        <ActivityIndicator size="large" style={styles.loader} />
      ) : (
        <Button
          title="Save Notification"
          onPress={handleSave}
          containerStyle={styles.button}
          buttonStyle={{ backgroundColor: theme.colors.primary }}
          icon={<Icon name="save" type="feather" color="#fff" />}
          disabled={!title || !message}
        />
      )}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: { padding: baseTheme.spacing.large },
  header: { alignSelf: 'center', marginBottom: baseTheme.spacing.large },
  button: { marginTop: baseTheme.spacing.medium },
  label: { marginTop: baseTheme.spacing.medium, fontWeight: 'bold' },
  picker: { backgroundColor: '#fff', borderRadius: 4 },
  previewHeader: { marginTop: baseTheme.spacing.large, fontSize: 18, fontWeight: '600' },
  previewBox: { marginTop: baseTheme.spacing.small, padding: baseTheme.spacing.medium, backgroundColor: baseTheme.colors.backgroundLight, borderRadius: 8 },
  previewTitle: { fontSize: 16, fontWeight: 'bold' },
  previewMessage: { fontSize: 14, marginTop: baseTheme.spacing.small },
  loader: { marginTop: baseTheme.spacing.medium },
});

const NotificationAddScreenWithTheme = (props) => (
  <ThemeProvider>
    <NotificationAddScreen {...props} />
  </ThemeProvider>
);

export default NotificationAddScreenWithTheme;
