import React, { useState } from 'react';
import { View, StyleSheet, TouchableOpacity, Text } from 'react-native';
import { Drawer, IconButton } from 'react-native-paper';
import { Ionicons } from '@expo/vector-icons';
import theme from '../../theme';

const sections = [
  {
    key: 'content',
    label: 'Content',
    icon: 'tv-outline',
    sub: [
      { key: 'series', label: 'Series List', icon: 'list-outline' },
      { key: 'seasons', label: 'Seasons', icon: 'albums-outline' },
      { key: 'episodes', label: 'Episodes', icon: 'film-outline' },
    ],
  },
  {
    key: 'users',
    label: 'Users',
    icon: 'people-outline',
    sub: [],
  },
  {
    key: 'analytics',
    label: 'Analytics',
    icon: 'bar-chart-outline',
    sub: [],
  },
  {
    key: 'publishing',
    label: 'Publishing',
    icon: 'calendar-outline',
    sub: [],
  },
  {
    key: 'settings',
    label: 'Settings',
    icon: 'settings-outline',
    sub: [],
  },
];

const Sidebar = ({ navigation, activeSection, onSectionChange }) => {
  const [collapsed, setCollapsed] = useState(false);
  const [expanded, setExpanded] = useState({});

  const handleSectionPress = (key) => {
    setExpanded((prev) => ({ ...prev, [key]: !prev[key] }));
    if (onSectionChange) onSectionChange(key);
  };

  return (
    <View style={[styles.sidebar, collapsed && styles.collapsed]}>
      <View style={styles.toggleRow}>
        <IconButton
          icon={collapsed ? 'chevron-right' : 'chevron-left'}
          size={24}
          onPress={() => setCollapsed((c) => !c)}
          style={styles.toggleBtn}
        />
      </View>
      <Drawer.Section style={styles.drawerSection}>
        {sections.map((section) => (
          <View key={section.key}>
            <Drawer.Item
              label={collapsed ? undefined : section.label}
              icon={({ color, size }) => (
                <Ionicons name={section.icon} size={size} color={color} />
              )}
              active={activeSection === section.key}
              onPress={() => handleSectionPress(section.key)}
              style={activeSection === section.key ? styles.activeItem : null}
            />
            {section.sub.length > 0 && expanded[section.key] && !collapsed && (
              <View style={styles.subSection}>
                {section.sub.map((sub) => (
                  <TouchableOpacity
                    key={sub.key}
                    style={styles.subItem}
                    onPress={() => onSectionChange(sub.key)}
                  >
                    <Ionicons name={sub.icon} size={18} color={theme.colors.textSecondary} style={{ marginRight: 8 }} />
                    <Text style={styles.subLabel}>{sub.label}</Text>
                  </TouchableOpacity>
                ))}
              </View>
            )}
          </View>
        ))}
      </Drawer.Section>
    </View>
  );
};

const styles = StyleSheet.create({
  sidebar: {
    width: 240,
    backgroundColor: theme.colors.backgroundLight,
    paddingTop: theme.spacing.large,
    borderRightWidth: 1,
    borderRightColor: theme.colors.border,
    height: '100%',
  },
  collapsed: {
    width: 64,
  },
  toggleRow: {
    alignItems: 'flex-end',
    paddingHorizontal: 8,
    marginBottom: theme.spacing.medium,
  },
  toggleBtn: {
    margin: 0,
  },
  drawerSection: {
    flex: 1,
  },
  activeItem: {
    backgroundColor: theme.colors.primary + '22',
  },
  subSection: {
    paddingLeft: 32,
    marginBottom: 8,
  },
  subItem: {
    flexDirection: 'row',
    alignItems: 'center',
    paddingVertical: 6,
  },
  subLabel: {
    color: theme.colors.textSecondary,
    fontSize: theme.typography.fontSize.medium,
  },
});

export default Sidebar;
