import React, { useState } from 'react';
import { ScrollView, StyleSheet, Alert, View, ActivityIndicator } from 'react-native';
import { Input, Button, Text, Icon } from 'react-native-elements';
import { useNavigation } from '@react-navigation/native';
import theme from '../../theme';
import { Picker } from '@react-native-picker/picker';
import DateTimePickerModal from 'react-native-modal-datetime-picker';
import axios from 'axios';

const NotificationAddScreen = () => {
  const navigation = useNavigation();
  const [title, setTitle] = useState('');
  const [message, setMessage] = useState('');
  const [audience, setAudience] = useState('');
  const [type, setType] = useState('');
  const [scheduleDate, setScheduleDate] = useState('');
  const [audienceOptions] = useState(['All Users', 'Subscribed Users', 'New Users', 'Region-Based']);
  const [typeOptions] = useState(['Push', 'Email', 'In-App']);
  const [sending, setSending] = useState(false);
  const [showDatePicker, setShowDatePicker] = useState(false);

  const handleSave = async () => {
    if (!title || !message) {
      Alert.alert('Validation error', 'Title and message are required');
      return;
    }
    setSending(true);
    try {
      await axios.post('https://your-backend.com/api/notifications', { title, message, audience, type, scheduleDate });
      Alert.alert('Success', 'Notification sent successfully');
      navigation.navigate('Notifications');
    } catch (error) {
      Alert.alert('Error', error.response?.data?.message || 'Failed to send notification');
    } finally {
      setSending(false);
    }
  };

  const handleConfirm = (date) => {
    setScheduleDate(date.toISOString());
    setShowDatePicker(false);
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text h4 style={styles.header}>Create New Notification</Text>
      <Input placeholder="Title" value={title} onChangeText={setTitle} />
      <Input placeholder="Message" value={message} onChangeText={setMessage} multiline />
      <Text style={styles.label}>Audience</Text>
      <Picker selectedValue={audience} onValueChange={setAudience} style={styles.picker}>
        {audienceOptions.map(opt => <Picker.Item label={opt} value={opt} key={opt} />)}
      </Picker>
      <Text style={styles.label}>Type</Text>
      <Picker selectedValue={type} onValueChange={setType} style={styles.picker}>
        {typeOptions.map(opt => <Picker.Item label={opt} value={opt} key={opt} />)}
      </Picker>
      <Text style={styles.label}>Schedule Date</Text>
      <Button
        title={scheduleDate ? `Scheduled: ${new Date(scheduleDate).toLocaleString()}` : 'Select Date'}
        onPress={() => setShowDatePicker(true)}
        type="outline"
      />
      <DateTimePickerModal
        isVisible={showDatePicker}
        mode="datetime"
        onConfirm={handleConfirm}
        onCancel={() => setShowDatePicker(false)}
      />
      <Text style={styles.previewHeader}>Preview</Text>
      <View style={styles.previewBox}>
        <Text style={styles.previewTitle}>{title || 'Title Preview'}</Text>
        <Text style={styles.previewMessage}>{message || 'Message Preview'}</Text>
      </View>
      {sending ? (
        <ActivityIndicator size="large" style={styles.loader} />
      ) : (
        <Button
          title="Save Notification"
          onPress={handleSave}
          containerStyle={styles.button}
          buttonStyle={{ backgroundColor: theme.colors.primary }}
          icon={<Icon name="save" type="feather" color="#fff" />}
          disabled={!title || !message}
        />
      )}
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: { padding: theme.spacing.large, backgroundColor: theme.colors.background },
  header: { alignSelf: 'center', marginBottom: theme.spacing.large },
  button: { marginTop: theme.spacing.medium },
  label: { marginTop: theme.spacing.medium, fontWeight: 'bold', color: theme.colors.text },
  picker: { backgroundColor: '#fff', borderRadius: 4 },
  previewHeader: { marginTop: theme.spacing.large, fontSize: 18, fontWeight: '600', color: theme.colors.text },
  previewBox: { marginTop: theme.spacing.small, padding: theme.spacing.medium, backgroundColor: '#fff', borderRadius: 8 },
  previewTitle: { fontSize: 16, fontWeight: 'bold', color: theme.colors.text },
  previewMessage: { fontSize: 14, marginTop: theme.spacing.small, color: theme.colors.textSecondary },
  loader: { marginTop: theme.spacing.medium },
});

export default NotificationAddScreen;
