import React, { useState, useEffect } from 'react';
import { View, StyleSheet, Image, ScrollView, Alert } from 'react-native';
import { Input, Button, Text, Icon } from 'react-native-elements';
import * as ImagePicker from 'expo-image-picker';
import { storage, db } from '../../utils/firebase';
import { ref, uploadBytes, getDownloadURL, deleteObject } from 'firebase/storage';
import { doc, getDoc, updateDoc } from 'firebase/firestore';

const SeriesEditScreen = ({ route, navigation }) => {
  const { id } = route.params;
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [thumbnail, setThumbnail] = useState(null);
  const [existingThumbnail, setExistingThumbnail] = useState(null);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    const fetchSeries = async () => {
      try {
        const docRef = doc(db, 'series', id);
        const snap = await getDoc(docRef);
        if (snap.exists()) {
          const data = snap.data();
          setTitle(data.title);
          setDescription(data.description);
          setThumbnail(data.thumbnailUrl);
          setExistingThumbnail(data.thumbnailUrl);
        }
      } catch (error) {
        console.error('Error fetching series:', error);
      } finally {
        setLoading(false);
      }
    };
    fetchSeries();
  }, [id]);

  const pickThumbnail = async () => {
    const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
    if (status !== 'granted') {
      Alert.alert('Permission required', 'Camera roll permission is required to select thumbnail.');
      return;
    }
    const result = await ImagePicker.launchImageLibraryAsync({ mediaTypes: ImagePicker.MediaTypeOptions.Images, allowsEditing: true, quality: 0.8 });
    if (!result.canceled) {
      setThumbnail(result.assets[0].uri);
    }
  };

  const handleSave = async () => {
    if (!title.trim()) {
      Alert.alert('Validation', 'Please enter a title for the series.');
      return;
    }
    setSaving(true);
    try {
      let thumbnailUrl = existingThumbnail;
      if (thumbnail && thumbnail !== existingThumbnail) {
        // optional: delete old thumbnail
        if (existingThumbnail) {
          try { await deleteObject(ref(storage, existingThumbnail)); } catch {};
        }
        const response = await fetch(thumbnail);
        const blob = await response.blob();
        const fileName = `series_${Date.now()}`;
        const storageRef = ref(storage, `series/${fileName}`);
        await uploadBytes(storageRef, blob);
        thumbnailUrl = await getDownloadURL(storageRef);
      }
      const docRef = doc(db, 'series', id);
      await updateDoc(docRef, { title: title.trim(), description: description.trim(), thumbnailUrl });
      Alert.alert('Success', 'Series updated successfully.');
      navigation.goBack();
    } catch (error) {
      console.error('Error updating series:', error);
      Alert.alert('Error', 'Could not update series. Please try again.');
    } finally {
      setSaving(false);
    }
  };

  if (loading) return <Text>Loading...</Text>;

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text h4 style={styles.header}>Edit Series</Text>
      <Input placeholder="Title" value={title} onChangeText={setTitle} leftIcon={{ name: 'edit', type: 'feather', color: '#555' }} />
      <Input placeholder="Description" value={description} onChangeText={setDescription} multiline leftIcon={{ name: 'align-left', type: 'feather', color: '#555' }} />
      <Button icon={<Icon name="image" type="feather" color="#fff" />} title="Change Thumbnail" onPress={pickThumbnail} containerStyle={styles.button} />
      {thumbnail && <Image source={{ uri: thumbnail }} style={styles.preview} />}
      <Button title={saving ? 'Saving...' : 'Save Changes'} onPress={handleSave} disabled={saving} containerStyle={styles.button} buttonStyle={{ backgroundColor: '#fc5c65' }} />
    </ScrollView>
  );
};

const styles = StyleSheet.create({
  container: { padding: 16, backgroundColor: '#fff' },
  header: { marginBottom: 16, alignSelf: 'center' },
  button: { marginVertical: 8 },
  preview: { width: '100%', height: 200, borderRadius: 8, marginTop: 8 },
});

export default SeriesEditScreen;
