import React, { useState, useEffect } from 'react';
import { View, StyleSheet, ScrollView, ActivityIndicator } from 'react-native';
import { Text, Card, Badge } from 'react-native-elements';
import { useNavigation } from '@react-navigation/native';
import HeaderBar from '../../components/common/HeaderBar';
import Sidebar from '../../components/common/Sidebar';
import { ThemeProvider, useTheme } from '../../components/common/ThemeProvider';
import paymentsApi from '../../api/paymentsApi';
import baseTheme from '../../theme';

const PaymentMethodsScreen = () => {
  const navigation = useNavigation();
  const { theme, toggleTheme } = useTheme();
  const [payments, setPayments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [activeSection, setActiveSection] = useState('payments');

  useEffect(() => {
    const fetchPayments = async () => {
      try {
        const data = await paymentsApi.getPayments();
        setPayments(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error('Error fetching payments:', err);
      } finally {
        setLoading(false);
      }
    };
    fetchPayments();
  }, []);

  // Group by payment method
  const grouped = payments.reduce((acc, p) => {
    const method = p.method || 'Other';
    if (!acc[method]) acc[method] = [];
    acc[method].push(p);
    return acc;
  }, {});

  return (
    <View style={[styles.container, { backgroundColor: theme.colors.background }]}>      
      <HeaderBar
        onToggleTheme={toggleTheme}
        isDarkMode={theme.dark}
        notificationCount={0}
        onLogout={() => {}}
        onSettings={() => {}}
      />
      <View style={styles.body}>
        <Sidebar
          navigation={navigation}
          activeSection={activeSection}
          onSectionChange={setActiveSection}
        />
        <ScrollView style={styles.content} contentContainerStyle={{ padding: baseTheme.spacing.large }}>
          <Text h4 style={[styles.title, { color: theme.colors.primary }]}>Payments by Method</Text>
          {loading ? (
            <ActivityIndicator size="large" color={theme.colors.primary} />
          ) : (
            Object.keys(grouped).map(method => (
              <View key={method}>
                <Text h5 style={[styles.methodTitle, { color: theme.colors.text }]}>{method}</Text>
                {grouped[method].map(p => (
                  <Card
                    key={p.transactionId}
                    containerStyle={[styles.card, { backgroundColor: theme.colors.backgroundLight }]}
                  >
                    <View style={styles.row}>
                      <Text style={[styles.text, { color: theme.colors.text }]}>ID: {p.transactionId}</Text>
                      <Badge
                        value={p.status}
                        status={p.status === 'Paid' ? 'success' : 'warning'}
                      />
                    </View>
                    <View style={styles.row}>
                      <Text style={[styles.text, { color: theme.colors.textSecondary }]}>{p.userEmail}</Text>
                      <Text style={[styles.text, { color: theme.colors.success }]}>${p.amount}</Text>
                    </View>
                    <Text style={[styles.text, { color: theme.colors.textSecondary }]}>Subscription: {p.subscriptionDuration || 'N/A'}</Text>
                    <Text style={[styles.text, { color: theme.colors.textSecondary }]}>{new Date(p.date).toLocaleString()}</Text>
                  </Card>
                ))}
              </View>
            ))
          )}
        </ScrollView>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: { flex: 1 },
  body: { flex: 1, flexDirection: 'row' },
  content: { flex: 1 },
  title: { marginBottom: baseTheme.spacing.large, alignSelf: 'center' },
  methodTitle: { marginTop: baseTheme.spacing.medium, marginBottom: baseTheme.spacing.small, fontWeight: 'bold' },
  card: { borderRadius: baseTheme.borderRadius.medium, marginBottom: baseTheme.spacing.medium },
  row: { flexDirection: 'row', justifyContent: 'space-between', marginBottom: baseTheme.spacing.small },
  text: {},
});

const PaymentMethodsScreenWithTheme = props => (
  <ThemeProvider>
    <PaymentMethodsScreen {...props} />
  </ThemeProvider>
);

export default PaymentMethodsScreenWithTheme;
